int main(int argc, char** argv) { glutInit(&argc, argv); // Required initialization! glutInitDisplayMode(GLUT_DOUBLE | GLUT_DEPTH); glutInitWindowSize(500,500); // size of display area, in pixels glutInitWindowPosition(100,100); // location in screen coordinates glutCreateWindow("OpenGL Program"); // the parameter is the window title
glutDisplayFunc(display); // called when window needs to be redrawn glutReshapeFunc(reshape); // called when size of the window changes glutKeyboardFunc(keyFunc); // called when user types a character glutSpecialFunc(specialKeyFunc);// called when user presses a special key glutMouseFunc(mouseFunc); // called for mousedown and mouseup events glutMotionFunc(mouseDragFunc); // called when mouse is dragged glutIdleFunc(idleFun); // called when there are no other events
glutMainLoop(); // Run the event loop! This function never returns. return 0; // (This line will never actually be reached.) }

void mouseFunc(int button, int buttonState, int x, int y) {
